import { Heading, HStack, Image, Text, VStack } from "@chakra-ui/react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowRight } from "@fortawesome/free-solid-svg-icons";
import React from "react";
import FullScreenSection from "./FullScreenSection";

const Card = ({ title, description, imageSrc }) => {
  // Implement the UI for the Card component according to the instructions.
  // You should be able to implement the component with the elements imported above.
  // Feel free to import other UI components from Chakra UI if you wish to.
  return (
    <FullScreenSection>
        <VStack textAlign="left" align="start" bg="white" overflow="hidden" borderRadius="lg" display="grid">
          <Image width="100%" src={imageSrc} />
          <Heading paddingLeft="4">{title}</Heading>
          <Text paddingLeft="4">{description}</Text>
          <Text paddingLeft="4">See more <FontAwesomeIcon icon={faArrowRight} size="1x" /> </Text>
        </VStack>
    </FullScreenSection>
  );
};

export default Card;
