import { Heading, HStack, Image, Text, VStack, Box } from "@chakra-ui/react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowRight } from "@fortawesome/free-solid-svg-icons";
import React from "react";

const Card = ({ title, description, imageSrc }) => {
  // Implement the UI for the Card component according to the instructions.
  // You should be able to implement the component with the elements imported above.
  // Feel free to import other UI components from Chakra UI if you wish to.
  return (
      <Box background="White"  borderRadius="2%">
        <VStack align="left" spacing={5}>
          <Image src={imageSrc} borderRadius="2%"/>
          <VStack align="left" spacing={4} paddingLeft="3" paddingBottom="3" maxHeight="infinity">
            <Text fontWeight="bold"  color="Black" align="top">{title}</Text>
            <Text color="Gray" align="bottom">{description}</Text>
            <Text color="Black" align="bottom">See more <FontAwesomeIcon icon={faArrowRight} size="1x"/></Text>
          </VStack>
        </VStack>
      </Box>
  );
};

export default Card;
