import React, { createElement, useEffect, useRef, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEnvelope } from "@fortawesome/free-solid-svg-icons";
import {
  faGithub,
  faLinkedin,
  faMedium,
  faStackOverflow,
} from "@fortawesome/free-brands-svg-icons";
import { Box, HStack } from "@chakra-ui/react";

const socials = [
  {
    icon: faEnvelope,
    url: "mailto: hello@example.com",
  },
  {
    icon: faGithub,
    url: "https://github.com",
  },
  {
    icon: faLinkedin,
    url: "https://www.linkedin.com",
  },
  {
    icon: faMedium,
    url: "https://medium.com",
  },
  {
    icon: faStackOverflow,
    url: "https://stackoverflow.com",
  },
];

const sections = [
  {
    name: "Project",
    key: "projects-section",
    link: "#projects",
    idp: "projects",
  },
  {
    name: "Contact Me",
    key: "contactme-section",
    link: "#contact-me",
    idp: "contactme",
  },
];

const Header = () => {
  const handleClick = (anchor) => () => {
    const id = `${anchor}-section`;
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({
        behavior: "smooth",
        block: "start",
      });
    }
  };

  const Socials = () => {
    const socialLinks = socials.map(social => {
      return (<Anchor key={social.url} id={social.url} link={social.url} icon={social.icon}/>);
    });

    return socialLinks;
  }

  const Sections = () => {
    const sectionLinks = sections.map(section => {
      return (<Anchor key={section.key} id={section.key} link={section.link} name={section.name} prefix={section.idp}/>);
    });

    return sectionLinks;
  }

  const Anchor = (props) => {
    if(props.icon){
      return (<a href={props.link} target="_blank"><FontAwesomeIcon icon={props.icon} size="2x" style={{ marginRight: "30px"}}/></a>)
    }else{
      return (<a href={props.link} onClick={handleClick(`${props.prefix}`)}>{props.name}</a>);
    }
  }

  const scrollRef = useRef(0);
  const [transfrom, setTransform] = useState("translateY(0)");

  useEffect(() => {
    const handleScroll = () => {
        if(window.scrollY > scrollRef.current){
          setTransform("translateY(-200px)");
        }else{
          setTransform("translateY(0)");
        }
        scrollRef.current = window.scrollY;
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll)
    };
  }, []);

  return (
    <Box
      position="fixed"
      top={0}
      left={0}
      right={0}
      translateY={0}
      transitionProperty="transform"
      transitionDuration=".3s"
      transitionTimingFunction="ease-in-out"
      backgroundColor="#18181b"
      transform={transfrom}
    >
      <Box color="white" maxWidth="1280px" margin="0 auto">
        <HStack
          px={16}
          py={4}
          justifyContent="space-between"
          alignItems="center"
        >
          <nav>
            {/* Add social media links based on the `socials` data */}
            <Socials/>
          </nav>
          <nav>
            <HStack spacing={8}>
              {/* Add links to Projects and Contact me section */}
              <Sections/>
            </HStack>
          </nav>
        </HStack>
      </Box>
    </Box>
  );
};
export default Header;
